app/src/main/java/com/finedine/rms/LoginActivity.java
```java:app/src/main/java/com/finedine/rms/LoginActivity.java
package com.finedine.rms;

import android.content.Intent;
import android.content.SharedPreferences;
import android.os.Build;
import android.os.Bundle;
import android.util.Log;
import android.view.View;
import android.widget.EditText;
import android.widget.Toast;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;

import com.finedine.rms.R;
import com.google.firebase.analytics.FirebaseAnalytics;

import javax.crypto.SecretKeyFactory;
import javax.crypto.spec.PBEKeySpec;
import java.security.NoSuchAlgorithmException;
import java.security.SecureRandom;
import java.security.spec.InvalidKeySpecException;
import java.security.spec.KeySpec;

public class LoginActivity extends AppCompatActivity {
 private EditText emailInput, passwordInput;
 private FirebaseAnalytics mFirebaseAnalytics;
 @Override
 protected void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 EdgeToEdge.enable(this);
 setContentView(R.layout.activity_login);
 mFirebaseAnalytics = FirebaseAnalytics.getInstance(this);
 emailInput = findViewById(R.id.emailInput);
 passwordInput = findViewById(R.id.passwordInput);
 }

 public void attemptLogin(View view) {
 String email = emailInput.getText().toString();
 String password = passwordInput.getText().toString();

 if (email.isEmpty() || password.isEmpty()) {
 Toast.makeText(this, "Please enter email and password", Toast.LENGTH_SHORT).show();
 return;
 }

 new Thread(() -> {
 // First just get the user by email
 User user = AppDatabase.getDatabase(this).userDao().getUserByEmail(email);

 runOnUiThread(() -> {
 if (user != null) {
 // Verify password - check if contains colon for proper PBKDF2 hash
 if (user.password_hash.contains(":")) {
 try {
 String[] parts = user.password_hash.split(":");
 byte[] salt = hexToBytes(parts[0]);
 String calculatedHash = hashPasswordWithSalt(password, salt);
 if ((parts[0] + ":" + calculatedHash).equals(user.password_hash)) {
 // Success
 saveUserSession(user);
 redirectToRoleHome(user.role);
 return;
 }
 } catch (Exception e) {
 Log.e("LoginActivity", "Error verifying password", e);
 }
 } 
 // Invalid credentials
 Toast.makeText(this, "Invalid credentials", Toast.LENGTH_SHORT).show();
 } else {
 Toast.makeText(this, "User not found", Toast.LENGTH_SHORT).show();
 }
 });
 }).start();
 }

 private String hashPassword(String plain) {
 try {
 byte[] salt = new byte[16];
 SecureRandom random = new SecureRandom();
 random.nextBytes(salt);
 KeySpec spec = new PBEKeySpec(plain.toCharArray(), salt, 65536, 128);
 SecretKeyFactory factory = SecretKeyFactory.getInstance("PBKDF2WithHmacSHA1");
 byte[] hash = factory.generateSecret(spec).getEncoded();
 return bytesToHex(salt) + ":" + bytesToHex(hash);
 } catch (NoSuchAlgorithmException | InvalidKeySpecException e) {
 Log.e("LoginActivity", "Error hashing password", e);
 return null;
 }
 }

 private String hashPasswordWithSalt(String plain, byte[] salt) {
 try {
 KeySpec spec = new PBEKeySpec(plain.toCharArray(), salt, 65536, 128);
 SecretKeyFactory factory = SecretKeyFactory.getInstance("PBKDF2WithHmacSHA1");
 byte[] hash = factory.generateSecret(spec).getEncoded();
 return bytesToHex(hash);
 } catch (NoSuchAlgorithmException | InvalidKeySpecException e) {
 Log.e("LoginActivity", "Error hashing password", e);
 return null;
 }
 }

 private byte[] hexToBytes(String hex) {
 int len = hex.length();
 byte[] data = new byte[len / 2];
 for (int i = 0; i < len; i += 2) {
 data[i / 2] = (byte) ((Character.digit(hex.charAt(i), 16) << 4)
 + Character.digit(hex.charAt(i+1), 16));
 }
 return data;
 }

 private String bytesToHex(byte[] bytes) {
 StringBuilder hexString = new StringBuilder();
 for (byte b : bytes) {
 String hex = Integer.toHexString(0xff & b);
 if (hex.length() == 1) {
 hexString.append('0');
 }
 hexString.append(hex);
 }
 return hexString.toString();
 }

 private void saveUserSession(User user) {
 SharedPreferences prefs = getSharedPreferences("FineDinePrefs", MODE_PRIVATE);

 prefs.edit()
 .putInt("userId", user.user_id)
 .putString("userRole", user.role)
 .putString("userName", user.name)
 .apply();
 }

 private void redirectToRoleHome(String role) {
 Intent intent;
 switch (role){
 case "waiter":
 intent = new Intent(this, OrderActivity.class);
 break;
 case "chef":
 intent = new Intent(this, KitchenActivity.class);
 break;
 case "manager":
 intent = new Intent(this, ManagerDashboardActivity.class);
 break;
 default: // customer
 intent = new Intent(this, Reservation.class);
 }
 startActivity(intent);
 finish();
 }

 public void goToRegister(View view) {
 startActivity(new Intent(this, RegisterActivity.class));
 }
}
```
</especially_relevant_code_snippet>

