app/src/main/java/com/finedine/rms/NotificationHelper.java
```java:app/src/main/java/com/finedine/rms/NotificationHelper.java
package com.finedine.rms;

import android.app.NotificationChannel;
import android.app.NotificationManager;
import android.app.PendingIntent;
import android.content.Context;
import android.content.Intent;
import android.os.Build;

import androidx.core.app.NotificationCompat;

import com.finedine.rms.R;


public class NotificationHelper {
 private final Context mContext;
 private final NotificationManager mNotificationManager;
 private static final String CHANNEL_ID = "finedine_channel_01";

 public NotificationHelper(Context context) {
 mContext = context;
 mNotificationManager = (NotificationManager) context.getSystemService(Context.NOTIFICATION_SERVICE);

 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {
 NotificationChannel channel = new NotificationChannel(
 CHANNEL_ID,
 "FineDine Notifications",
 NotificationManager.IMPORTANCE_HIGH);
 mNotificationManager.createNotificationChannel(channel);
 }
 }

 public void sendNotification(String title, String message, Class<?> cls) {
 Intent intent = new Intent(mContext, cls);
 intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
 PendingIntent pendingIntent = PendingIntent.getActivity(mContext, 0, intent, PendingIntent.FLAG_IMMUTABLE);

 NotificationCompat.Builder builder = new NotificationCompat.Builder(mContext, CHANNEL_ID)
 // .setSmallIcon(R.drawable.ic_notification)
 .setContentTitle(title)
 .setContentText(message)
 .setPriority(NotificationCompat.PRIORITY_HIGH)
 .setContentIntent(pendingIntent)
 .setAutoCancel(true);

 mNotificationManager.notify((int) System.currentTimeMillis(), builder.build());
 }
}
