 if (user != null) {
 // Verify password - check if contains colon for proper PBKDF2 hash
 if (user.password_hash.contains(":")) {
 try {
 String[] parts = user.password_hash.split(":");
 byte[] salt = hexToBytes(parts[0]);
 String calculatedHash = hashPasswordWithSalt(password, salt);
 if ((parts[0] + ":" + calculatedHash).equals(user.password_hash)) {
 // Success
 saveUserSession(user);
 redirectToRoleHome(user.role);
 return;
 }
 } catch (Exception e) {
 Log.e("LoginActivity", "Error verifying password", e);
 }
 } else if (String.valueOf(password.hashCode()).equals(user.password_hash)) {
 // Handle legacy password format (plain hash without salt)
 saveUserSession(user);
 redirectToRoleHome(user.role);
 return;
 }
 // Invalid credentials
 Toast.makeText(this, "Invalid password", Toast.LENGTH_SHORT).show();
 } else {
 Toast.makeText(this, "User not found. Please check your email or register.", Toast.LENGTH_SHORT).show();
