```java:app/src/main/java/com/finedine/rms/LoginActivity.java
package com.finedine.rms;

import android.os.Bundle;
import android.widget.Button;
import android.widget.EditText;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

import com.finedine.rms.utils.SharedPrefsManager;

public class LoginActivity extends AppCompatActivity {

 private EditText emailField;
 private EditText passwordField;
 private Button loginButton;

 @Override
 protected void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 setContentView(R.layout.activity_login);

 // Initialize views
 emailField = findViewById(R.id.email_field);
 passwordField = findViewById(R.id.password_field);
 loginButton = findViewById(R.id.login_button);

 loginButton.setOnClickListener(v -> {
 String email = emailField.getText().toString().trim();
 String password = passwordField.getText().toString().trim();

 if (email.isEmpty() || password.isEmpty()) {
 Toast.makeText(this, "Please enter email and password", Toast.LENGTH_SHORT).show();
 return;
 }

 // Here you would normally authenticate with Firebase
 // For now, just show a success message
 Toast.makeText(this, "Login successful", Toast.LENGTH_SHORT).show();

 // Save user session (demo values)
 SharedPrefsManager prefsManager = new SharedPrefsManager(this);
 prefsManager.saveUserSession(1, "manager", "Demo User");
 });
 }
}
```

