 // For now, just accept any credentials
 Toast.makeText(this, "Login successful", Toast.LENGTH_SHORT).show();

 // Save user session
 SharedPrefsManager prefsManager = new SharedPrefsManager(this);
 prefsManager.saveUserSession(1, "manager", email);

 // Navigate to main screen (adjust this to your actual main activity)
 // Intent intent = new Intent(this, MainActivity.class);
 // startActivity(intent);
 // finish();
 });

 // Set click listener for register link if it exists
 if (registerLink != null) {
 registerLink.setOnClickListener(v -> {
 Toast.makeText(this, "Register functionality coming soon", Toast.LENGTH_SHORT).show();
 // Intent intent = new Intent(this, RegisterActivity.class);
 // startActivity(intent);
 });
 }
 }
}
