app/src/main/java/utils/FileUtils.java
```java:app/src/main/java/utils/FileUtils.java
package utils;

import android.annotation.SuppressLint;
import android.content.Context;
import android.database.Cursor;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.net.Uri;
import android.provider.OpenableColumns;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.IOException;

public class FileUtils {
 public static String saveImageToInternalStorage(Context context, Bitmap bitmap, String fileName) {
 File directory = context.getDir("finedine_images", Context.MODE_PRIVATE);
 File file = new File(directory, fileName + ".jpg");

 try (FileOutputStream fos = new FileOutputStream(file)) {
 bitmap.compress(Bitmap.CompressFormat.JPEG, 90, fos);
 return file.getAbsolutePath();
 } catch (IOException e) {
 e.printStackTrace();
 return null;
 }
 }

 public static Bitmap loadImageFromStorage(Context context, String path) {
 try {
 File file = new File(path);
 return BitmapFactory.decodeStream(new FileInputStream(file));
 } catch (FileNotFoundException e) {
 e.printStackTrace();
 return null;
 }
 }

 public static void deleteImageFromStorage(Context context, String path) {
 if (path != null) {
 File file = new File(path);
 if (file.exists()) {
 file.delete();
 }
 }
 }

 @SuppressLint("Range")
 public static String getFileNameFromUri(Context context, Uri uri) {
 String result = null;
 if (uri.getScheme().equals("content")) {
 try (Cursor cursor = context.getContentResolver().query(uri, null, null, null, null)) {
 if (cursor != null && cursor.moveToFirst()) {
 result = cursor.getString(cursor.getColumnIndex(OpenableColumns.DISPLAY_NAME));
 }
 }
 }
 if (result == null) {
 result = uri.getPath();
 int cut = result.lastIndexOf('/');
 if (cut != -1) {
 result = result.substring(cut + 1);
 }
 }
 return result;
 }
}
```
</especially_relevant_code_snippet>

